import React from "react";
import ReactDOM from "react-dom";
import { Formik, Field, Form } from "formik";

const sleep = (ms) => new Promise((r) => setTimeout(r, ms));
const toggle = "toggle";

const preferencesList = [
  "vegetarian",
  "gluten free",
  "ketogenic",
  "vegan",
  "pescetarian",
  "paleo",
  "dairy",
  "egg",
  "gluten",
  "grain",
  "peanut",
  "seafood",
  "sesame",
  "shellfish",
  "soy",
  "sulphite",
  "tree nuts",
  "wheat",
];

const mappedValues = preferencesList.map((preference) => ({
  [preference]: false,
}));

const Preferences = () => (
  <div>
    <h1>Sign Up</h1>
    <Formik
      initialValues={mappedValues}
      onSubmit={async (values) => {
        await sleep(500);
        alert(JSON.stringify(values, null, 2));
      }}
    >
      {({ values }) => {
        // console.log("rest of props", values);
        console.log("value", values);
        return (
          <Form>
            {/* 
            This first checkbox will result in a boolean value being stored. Note that the `value` prop
            on the <Field/> is omitted
          */}
            {preferencesList.map((preference, index) => (
              <label key={index}>
                <Field type="checkbox" name={preference} />
                {preference}
              </label>
            ))}

            {/* 
            Multiple checkboxes with the same name attribute, but different
            value attributes will be considered a "checkbox group". Formik will automagically
            bind the checked values to a single array for your benefit. All the add and remove
            logic will be taken care of for you.
          */}
            {/* <div id="checkbox-group">Checked</div>
          <div role="group" aria-labelledby="checkbox-group">
            <label>
              <Field type="checkbox" name="checked" value="One" />
              One
            </label>
            <label>
              <Field type="checkbox" name="checked" value="Two" />
              Two
            </label>
            <label>
              <Field type="checkbox" name="checked" value="Three" />
              Three
            </label>
          </div> */}

            <button type="submit">Submit</button>
          </Form>
        );
      }}
    </Formik>
  </div>
);

ReactDOM.render(<Preferences />, document.getElementById("root"));

export default Preferences;
